function countUp(o){$(o).each(function(){$(this).prop("Counter",0).animate({Counter:$(this).text()},{duration:2e3,easing:"swing",step:function(o){$(this).text(Math.ceil(o))}})})}function checkVisible(o,e){e=e||"object visible";var t=$(window).height(),n=$(window).scrollTop(),i=$(o).offset().top,c=$(o).height();return"object visible"==e?i<t+n&&i>n-c:"above"==e?i<t+n:void 0}var $=jQuery;$(".counter_row").length?$(window).on("scroll",function(){checkVisible($(".counter_row"))&&(countUp(".count"),$(window).off("scroll"))}):console.log("No numbers here...");